                if (user.hasOwnProperty(k.split('_')[0])) user[k] = user.hasOwnProperty(k) ? !!Number(user[k]) : false;
                else delete user[k];
            }
            // else if (k === 'email_subscription') user[k] = user.hasOwnProperty(k) ? !!Number(user[k]) : false;
            else user[k] = user.hasOwnProperty(k) ? Number(user[k]) : 0;
        }

        for (let k of [
            'email',
            'phone_number'
        ]) {
            if (user.hasOwnProperty(k)) {
                if (user[k + '_verified'] === true || user[k + '_verified'] === 'true') {
                    user[k + '_verified'] = true;
                }
                else {
                    user[k + '_verified'] = false;
                }
            }
            else {
                delete user[k + '_verified'];
            }
        }

        for (let k of [
            'aud',
            { from: 'auth_time', to: 'log' },
            'cognito:username',
            'event_id',
            'exp',
            'iat',
            'iss',
            'jti',
            'origin_jti',
            'secret_key',
            { from: 'sub', to: 'user_id' },
            'token_use'
        ]) {
            if (typeof k === 'string') {
                delete user[k];
            }
            else {
                user[k.to] = user[k.from];
                delete user[k.from];
            }
        };

        this.__user = user;
    };

    const getUser = (): Promise<UserProfile | null> => {
        // get users updated attribute

        return new Promise((res, rej) => {
            if (!this.session) { res(null); }
            if (cognitoUser === null) { rej(new SkapiError('Invalid session', { code: 'INVALID_REQUEST' })); }
            else {
                cognitoUser.getUserAttributes((attrErr, attributes) => {
                    if (attrErr) rej(attrErr);
                    else {
                        normalizeUserAttributes(attributes);
                        res(this.user);
                    }
                });
            }
        });
    };

    const getSession = async (option?: {
        refreshToken?: boolean;
    }): Promise<CognitoUserSession> => {
        // fetch session, updates user attributes
        let { refreshToken = false } = option || {};

        return new Promise((res, rej) => {
            cognitoUser = userPool?.getCurrentUser() || null;

            if (cognitoUser === null) {
                rej(null);
                return;
            }

            cognitoUser.getSession((err: any, session: CognitoUserSession) => {
                if (err) {
                    rej(err);
                    return;
                }

                if (!session) {
                    rej(new SkapiError('Current session does not exist.', { code: 'INVALID_REQUEST' }));
                    return;
                }

                let respond = (session) => {
                    let idToken = session.getIdToken().payload;

                    if (idToken['custom:service'] !== this.service) {
                        cognitoUser.signOut();
                        this.session = null;
                        rej(new SkapiError('Invalid session.', { code: 'INVALID_REQUEST' }));
                        return;
                    }

                    this.session = session;
                    normalizeUserAttributes(idToken);
                    res(session);
                }
                // try refresh when invalid token
                if (refreshToken || !session.isValid()) {
                    cognitoUser.refreshSession(session.getRefreshToken(), (refreshErr, refreshedSession) => {
                        if (refreshErr) {
                            rej(refreshErr);
                        }
                        else {
                            if (refreshedSession.isValid()) {
                                return respond(refreshedSession);
                                // session = refreshedSession;
                            }
                            else {
                                rej(new SkapiError('Invalid session.', { code: 'INVALID_REQUEST' }));
                                return;
                            }
                        }
                    });
                }
                else {
                    return respond(session);
                }
            });
        });
    };

    const createCognitoUser = (un: string) => {
        let username = un.includes(this.service + '-') ? un : this.service + '-' + MD5.hash(un);

        return {
            cognitoUser: new CognitoUser({
                Username: username,
                Pool: userPool
            }),
            cognitoUsername: username
        };
    };

    const authenticateUser = (email: string, password: string): Promise<UserProfile> => {
        return new Promise((res, rej) => {
            this.__request_signup_confirmation = null;
            this.__disabledAccount = null;

            let initUser = createCognitoUser(email);
            let username = initUser.cognitoUsername;
            let authenticationDetails = new AuthenticationDetails({
                Username: username,
                Password: password
            });

            initUser.cognitoUser.authenticateUser(authenticationDetails, {
                newPasswordRequired: (userAttributes, requiredAttributes) => {
                    this.__disabledAccount = null;
                    this.__request_signup_confirmation = username;
                    if (userAttributes['custom:signup_ticket'] === 'PASS' || userAttributes['custom:signup_ticket'] === 'MEMBER') {
                        // auto confirm - (setting password from admin created account)
                        initUser.cognitoUser.completeNewPasswordChallenge(password, {}, {
                            onSuccess: _ => {
                                cognitoUser = initUser.cognitoUser;
                                getSession().then(session => res(this.user));
                            },
                            onFailure: (err: any) => {
                                rej(new SkapiError(err.message || 'Failed to authenticate user.', { code: err.code }));
                            }
                        });
                    }
                    else {
                        // legacy method... will be deprecated
                        rej(new SkapiError("User's signup confirmation is required.", { code: 'SIGNUP_CONFIRMATION_NEEDED' }));
                    }
                },
                onSuccess: _ => getSession().then(_ => {
                    this.__disabledAccount = null;
                    res(this.user);
                }),
                onFailure: (err: any) => {
                    let error: [string, string] = [err.message || 'Failed to authenticate user.', err?.code || 'INVALID_REQUEST'];

                    if (err.code === "NotAuthorizedException") {
                        if (err.message === "User is disabled.") {
                            this.__disabledAccount = username;
                            error = ['This account is disabled.', 'USER_IS_DISABLED'];
                        }

                        else {
                            error = ['Incorrect username or password.', 'INCORRECT_USERNAME_OR_PASSWORD'];
                        }
                    }
                    else if (err.code === "UserNotFoundException") {
                        error = ['Incorrect username or password.', 'INCORRECT_USERNAME_OR_PASSWORD'];
                    }
                    else if (err.code === "UserNotConfirmedException") {
                        this.__request_signup_confirmation = username;
                        error = ["User's signup confirmation is required.", 'SIGNUP_CONFIRMATION_NEEDED'];
                    }
                    else if (err.code === "TooManyRequestsException" || err.code === "LimitExceededException") {
                        error = ['Too many attempts. Please try again later.', 'REQUEST_EXCEED'];
                    }

                    let errCode = error[1];
                    let errMsg = error[0];
                    let customErr = error[0].split('#');

                    // "#INVALID_REQUEST: the account has been blacklisted"
                    // "#NOT_EXISTS: the account does not exist"
                    // "#CONFIRM_REQUIRED": The account signup needs to be confirmed"

                    if (customErr.length > 1) {
                        customErr = customErr[customErr.length - 1].split(':');
                        errCode = customErr[0];
                        errMsg = customErr[1];
                        if (errCode === 'CONFIRM_REQUIRED') {
                            this.__request_signup_confirmation = username;
                            rej(new SkapiError("User's signup confirmation is required.", { code: 'SIGNUP_CONFIRMATION_NEEDED' }));
                            return;
                        }
                    }

                    rej(new SkapiError(errMsg, { code: errCode, cause: err }));
                }
            });
        });
    };

    const signup = (username: string, password: string, attributes: CognitoUserAttribute[]) => {
        return new Promise((res, rej) => {
            userPool.signUp(username, password, attributes, null, (err, result) => {
                if (err) {
                    rej(err);
                    return;
                }
                res(result);
                return;
            });
        })
    }

    return {
        getSession,
        authenticateUser,
        createCognitoUser,
        getUser,
        signup
    };
}

export async function getProfile(options?: { refreshToken: boolean; }): Promise<UserProfile | null> {
    await this.__authConnection;
    try {
        await authentication.bind(this)().getSession(options);
        return this.user;
    } catch (err) {
        return null;
    }
}

export async function checkAdmin() {
    await this.__connection;
    if (this.__user?.service === this.service) {
        // logged in
        return this.__user?.owner === this.host;
    } else {
        // not logged
        await logout.bind(this)();
    }

    return false;
}

export async function logout(): Promise<'SUCCESS: The user has been logged out.'> {
    await this.__connection;

    if (cognitoUser) {
        cognitoUser.signOut();
    }

    let to_be_erased = {
        'session': null,
        '__startKeyHistory': {},
        '__cached_requests': {},
        '__user': null
    };

    for (let k in to_be_erased) {
        this[k] = to_be_erased[k];
    }

    return 'SUCCESS: The user has been logged out.';
}

export async function resendSignupConfirmation(
    /** Redirect url on confirmation success. */
    redirect: string
): Promise<'SUCCESS: Signup confirmation E-Mail has been sent.'> {
    if (!this.__request_signup_confirmation) {
        throw new SkapiError('Least one login attempt is required.', { code: 'INVALID_REQUEST' });
    }

    if (redirect) {
        redirect = validator.Url(redirect);
    }
    else {
        redirect = undefined;
    }

    let resend = await request.bind(this)("confirm-signup", {
        username: this.__request_signup_confirmation,
        redirect
    });

    return resend; // 'SUCCESS: Signup confirmation E-Mail has been sent.'
}

export async function recoverAccount(
    /** Redirect url on confirmation success. */
    redirect: boolean | string = false
): Promise<"SUCCESS: Recovery e-mail has been sent."> {

    if (typeof redirect === 'string') {
        redirect = validator.Url(redirect);
    }

    else if (typeof redirect !== 'boolean') {
        throw new SkapiError('Argument should be type: <boolean | string>.', { code: 'INVALID_REQUEST' });
    }

    if (!this.__disabledAccount) {
        throw new SkapiError('Least one signin attempt of disabled account is required.', { code: 'INVALID_REQUEST' });
    }

    await request.bind(this)("recover-account", { username: this.__disabledAccount, redirect });
    return 'SUCCESS: Recovery e-mail has been sent.';
}

export async function jwtLogin(params: {
    idToken: string;
    keyUrl: string;
    clientId: string;
    provider: string;
    nonce?: string;
}) {
    validator.Params(params, {
        idToken: 'string',
        clientId: 'string',
        keyUrl: (v: string) => validator.Url(v),
        provider: 'string',
        nonce: 'string'
    }, ['idToken', 'keyUrl', 'clientId']);

    let { hashedPassword, username, email } = await request.bind(this)("jwt-login", params);
    try {
        return login.bind(this)({ username: username, password: hashedPassword, email });
    }
    catch (err: SkapiError | any) {
        if (err?.code === 'INCORRECT_USERNAME_OR_PASSWORD') {
            throw new SkapiError('User has migrated the account. Login with the service email and password.', { code: 'INVALID_REQUEST' });
        }
    }
}

export async function login(
    form: Form<{
        /** if given, username will be used instead of email. */
        username?: string;
        /** E-Mail for signin. 64 character max. */
        email: string;
        /** Password for signin. Should be at least 6 characters. */
        password: string;
    }>): Promise<UserProfile> {
    await this.logout();
    let params = validator.Params(form, {
        username: 'string',
        email: 'string',
        password: 'string'
    }, ['password']);

    if (params.email) {
        // incase user uses email instead of username
        try {
            validator.Email(params.email);
        } catch (err) {
            params.username = params.email;
            delete params.email;
        }
    }

    if (!params.username && !params.email) {
        throw new SkapiError('Least one of "username" or "email" is required.', { code: 'INVALID_PARAMETER' });
    }

    return authentication.bind(this)().authenticateUser(params.username || params.email, params.password);

    // INVALID_REQUEST: the account has been blacklisted.
    // NOT_EXISTS: the account does not exist.
}

export async function signup(
    form: Form<UserAttributes & { email: String; password: String; username?: string; }>,
    option?: {
        signup_confirmation?: boolean | string;
        email_subscription?: boolean;
        login?: boolean;
    }): Promise<UserProfile | "SUCCESS: The account has been created. User's signup confirmation is required." | 'SUCCESS: The account has been created.'> {

    let paramRestrictions = {
        username: 'string',
        password: (v: string) => validator.Password(v),

        email: (v: string) => validator.Email(v),
        name: 'string',
        address: (v: any) => {
            if (!v) return '';

            if (typeof v === 'string') {
                return v;
            }

            if (typeof v === 'object') {
                return JSON.stringify(v);
            }

            return undefined;
        },
        gender: 'string',
        birthdate: (v: string) => validator.Birthdate(v),
        phone_number: (v: string) => validator.PhoneNumber(v),

        email_public: ['boolean', () => false],
        address_public: ['boolean', () => false],
        gender_public: ['boolean', () => false],
        birthdate_public: ['boolean', () => false],
        phone_number_public: ['boolean', () => false],
        access_group: 'number',
        misc: 'string',

        picture: (v: string) => { if (v) return validator.Url(v); else return undefined },
        profile: (v: string) => { if (v) return validator.Url(v); else return undefined },
        family_name: 'string',
        given_name: 'string',
        middle_name: 'string',
        nickname: 'string',
        website: (v: string) => { if (v) return validator.Url(v); else return undefined },
    };

    let params = validator.Params(form || {}, paramRestrictions, ['email', 'password']);

    // always logout before creating an account (for users)
    await this.logout();

    option = validator.Params(option || {}, {
        email_subscription: (v: boolean) => {
            if (typeof v !== 'boolean') {
                throw new SkapiError('"option.email_subscription" should be type: <boolean>.', { code: 'INVALID_PARAMETER' });
            }
            if (!option?.signup_confirmation) {
                // requires to be url or true
                throw new SkapiError('"option.signup_confirmation" is required for email subscription.', { code: 'INVALID_PARAMETER' });
            }
            return v;
        },
        signup_confirmation: (v: string | boolean) => {
            let value = v;
            if (typeof v === 'string') {
                value = validator.Url(v);
            }
            else if (typeof v === 'boolean') {
                value = v;
            }
            else {
                throw new SkapiError('"option.signup_confirmation" should be type: <string | boolean>.', { code: 'INVALID_PARAMETER' });
            }

            if (value && !params.email) {
                throw new SkapiError('"email" is required for signup confirmation.', { code: 'INVALID_PARAMETER' });
            }

            return value;
        },
        login: (v: boolean) => {
            if (typeof v === 'boolean') {
                if (option.signup_confirmation && v) {
                    throw new SkapiError('"login" is not allowed when "option.signup_confirmation" is true.', { code: 'INVALID_PARAMETER' });
                }
                return v;
            }
            throw new SkapiError('"option.login" should be type: boolean.', { code: 'INVALID_PARAMETER' });
        }
    });

    let logUser = option?.login || false;

    params.signup_confirmation = option?.signup_confirmation || false;;
    params.email_subscription = option?.email_subscription || false;

    if (params.email_public && !params.signup_confirmation) {
        throw new SkapiError('"option.signup_confirmation" should be true if "email_public" is set to true.', { code: 'INVALID_PARAMETER' });
    }

    // cognito signup process below

    params.service = this.service;
    params.owner = this.owner;

    // user creating account
    let newUser = authentication.bind(this)().createCognitoUser(params.username || params.email);

    for (let k of ['email_public',
        'address_public',
        'gender_public',
        'birthdate_public',
        'phone_number_public']) {
        params[k] = params[k] ? '1' : '0';
    }

    if (params.access_group) {
        params.access_group = params.access_group.toString();
    }

    let signup_key = (await request.bind(this)('signupkey', {
        username: newUser.cognitoUsername,
        signup_confirmation: typeof params.signup_confirmation === 'boolean' ? JSON.stringify(params.signup_confirmation) : params.signup_confirmation,
        email_subscription: params.email_subscription,
    })).split(':');

    let signup_ticket = signup_key.slice(1).join(':');

    let attributeList = [
        new CognitoUserAttribute({
            Name: 'custom:signup',
            Value: signup_key[0]
        }),
        new CognitoUserAttribute({
            Name: 'locale',
            Value: signup_ticket.split('#')[1]
        }),
        new CognitoUserAttribute({
            Name: 'custom:signup_ticket',
            Value: signup_ticket
        })
    ];

    for (let k in paramRestrictions) {
        let customParams = [
            'email_public',
            'address_public',
            'gender_public',
            'birthdate_public',
            'phone_number_public',
            'misc',
            'service',
            'owner'
        ];
        if (params[k] === "") {
            continue;
        }

        if (k === 'username' || k === 'password' || k === 'access_group') {
            continue;
        }

        if (customParams.includes(k)) {
            attributeList.push(new CognitoUserAttribute({
                Name: 'custom:' + k,
                Value: params[k]
            }));
        }
        else {
            attributeList.push(new CognitoUserAttribute({
                Name: k,
                Value: params[k]
            }));
        }
    }

    await authentication.bind(this)().signup(newUser.cognitoUsername, params.password, attributeList);

    if (params.signup_confirmation) {
        cognitoUser = newUser.cognitoUser;
        this.__request_signup_confirmation = newUser.cognitoUsername;
        return "SUCCESS: The account has been created. User's signup confirmation is required.";
    }
    else if (logUser) {
        // log user in
        return login.bind(this)({ email: params.username || params.email, password: params.password });
    }

    return 'SUCCESS: The account has been created.';
}

export async function disableAccount(): Promise<'SUCCESS: account has been disabled.'> {
    await this.__connection;
    let result = await request.bind(this)('remove-account', { disable: this.__user.user_id }, { auth: true });
    await logout.bind(this)();
    return result;
}

export async function resetPassword(form: Form<{
    email: string;
    code: string | number;
    new_password: string;
}>): Promise<"SUCCESS: New password has been set."> {

    await this.__connection;

    let params = validator.Params(form, {
        email: (v: string) => validator.Email(v),
        code: ['number', 'string'],
        new_password: (v: string) => validator.Password(v)
    }, ['email', 'code', 'new_password']);

    let code = params.code, new_password = params.new_password;

    if (typeof code === 'number') {
        code = code.toString();
    }

    return new Promise(async (res, rej) => {
        let cognitoUser = authentication.bind(this)().createCognitoUser(params.email).cognitoUser;

        cognitoUser.confirmPassword(code, new_password, {
            onSuccess: result => {
                res("SUCCESS: New password has been set.");
            },
            onFailure: (err: any) => {
                rej(new SkapiError(err?.message || 'Failed to reset password.', { code: err?.code }));
            }
        });
    });
}

async function verifyAttribute(attribute: string, form: Form<{ code: string; }>): Promise<string> {
    await this.__connection;
    let code: string;

    if (!cognitoUser) {
        throw new SkapiError('The user has to be logged in.', { code: 'INVALID_REQUEST' });
    }

    if (attribute === 'email' || attribute === 'phone_number') {
        if (!this.__user.hasOwnProperty(attribute)) {
            throw new SkapiError(`No ${attribute === 'email' ? 'e-mail' : 'phone number'} to verify`, { code: 'INVALID_REQUEST' });
        }

        if (this.__user?.[`${attribute}_verified`]) {
            return `SUCCESS: "${attribute}" is verified.`;
        }

        code = (form ? validator.Params(form, {
            code: ['string']
        }) : {}).code || '';
    }
    else {
        return;
    }

    return new Promise((res, rej) => {
        let callback: any = {
            onSuccess: (result: any) => {
                if (code) {
                    authentication.bind(this)().getSession({ refreshToken: true }).then(
                        () => {
                            if (this.__user) {
                                this.__user[attribute + '_verified'] = true;
                            }
                            res(`SUCCESS: "${attribute}" is verified.`);
                        }
                    ).catch(err => {
                        rej(err);
                    });
                }

                else {
                    res('SUCCESS: Verification code has been sent.');
                }
            },
            onFailure: (err: Record<string, any>) => {
                rej(
                    new SkapiError(
                        err.message || 'Failed to request verification code.',
                        {
                            code: err?.code
                        }
                    )
                );
            }
        };

        if (code) {
            cognitoUser?.verifyAttribute(attribute, code, callback);
        }
        else {
            callback.inputVerificationCode = null;
            cognitoUser?.getAttributeVerificationCode(attribute, callback);
        }
    });
}

export function verifyPhoneNumber(form?: Form<{ code: string; }>): Promise<string> {
    // 'SUCCESS: Verification code has been sent.' | 'SUCCESS: "phone_number" is verified.'
    return verifyAttribute.bind(this)('phone_number', form);
}

export function verifyEmail(form?: Form<{ code: string; }>): Promise<string> {
    // 'SUCCESS: Verification code has been sent.' | 'SUCCESS: "email" is verified.'
    return verifyAttribute.bind(this)('email', form);
}

export async function forgotPassword(
    form: Form<{
        /** Signin E-Mail. */
        email: string;
    }>): Promise<"SUCCESS: Verification code has been sent."> {

    await this.__connection;

    let params = validator.Params(form, {
        email: (v: string) => validator.Email(v)
    }, ['email']);

    return new Promise(async (res, rej) => {
        let cognitoUser = authentication.bind(this)().createCognitoUser(params.email).cognitoUser;
        cognitoUser.forgotPassword({
            onSuccess: result => {
                res("SUCCESS: Verification code has been sent.");
            },
            onFailure: (err: any) => {
                rej(new SkapiError(err?.message || 'Failed to send verification code.', { code: err?.code || 'ERROR' }));
            }
        });
    });
}

export async function changePassword(params: {
    new_password: string;
    current_password: string;
}): Promise<'SUCCESS: Password has been changed.'> {
    await this.__connection;
    if (!this.session) {
        throw new SkapiError('User login is required.', { code: 'INVALID_REQUEST' });
    }

    let p = validator.Params(params, {
        'current_password': 'string',
        'new_password': 'string'
    });

    if (!p?.current_password) {
        throw new SkapiError('"current_password" is required to change password.', { code: 'INVALID_PARAMETER' });
    }

    if (!p?.new_password) {
        throw new SkapiError('"new_password" is required to change password.', { code: 'INVALID_PARAMETER' });
    }

    validator.Password(p.current_password);
    validator.Password(p.new_password);

    return new Promise((res, rej) => {
        cognitoUser.changePassword(
            p.current_password,
            p.new_password,
            (err: any, result: any) => {
                if (err) {
                    if (err?.code === "InvalidParameterException") {
                        rej(new SkapiError('Invalid password parameter.', { code: 'INVALID_PARAMETER' }));
                    }
                    else if (err?.code === "NotAuthorizedException") {
                        rej(new SkapiError('Incorrect password.', { code: 'INVALID_REQUEST' }));
                    }
                    else if (err?.code === "TooManyRequestsException" || err?.code === "LimitExceededException") {
                        rej(new SkapiError('Too many attempts. Please try again later.', { code: 'REQUEST_EXCEED' }));
                    }
                    else {
                        rej(new SkapiError(err?.message || 'Failed to change user password.', { code: err?.code || err?.name }));
                    }
                }

                res('SUCCESS: Password has been changed.');
            });
    });
}

export async function updateProfile(form: Form<UserAttributes>): Promise<UserProfile> {
    await this.__connection;
    if (!this.session) {
        throw new SkapiError('User login is required.', { code: 'INVALID_REQUEST' });
    }

    let params = validator.Params(form || {}, {
        email: (v: string) => validator.Email(v),
        address: (v: any) => {
            if (!v) return '';

            if (typeof v === 'string') {
                return v;
            }

            if (typeof v === 'object') {
                return JSON.stringify(v);
            }

            return undefined;
        },
        name: 'string',
        gender: 'string',
        birthdate: (v: string) => validator.Birthdate(v),
        phone_number: (v: string) => validator.PhoneNumber(v),
        email_public: 'boolean',
        phone_number_public: 'boolean',
        address_public: 'boolean',
        gender_public: 'boolean',
        birthdate_public: 'boolean',
        misc: 'string',

        picture: (v: string) => validator.Url(v),
        profile: (v: string) => validator.Url(v),
        family_name: 'string',
        given_name: 'string',
        middle_name: 'string',
        nickname: 'string',
        website: (v: string) => validator.Url(v),
    });

    if (params && typeof params === 'object' && !Object.keys(params).length) {
        return this.user;
    }

    // set alternative signin email
    if (params.email) {
        params['preferred_username'] = this.service + '-' + MD5.hash(params.email);
    }

    let collision = [
        ['email_public', 'email_verified', "User's E-Mail should be verified to set"],
        ['phone_number_public', 'phone_number_verified', "User's phone number should be verified to set"]
    ];

    if (this.__user) {
        for (let c of collision) {
            if (params[c[0]] && !this.__user[c[1]]) {
                throw new SkapiError(`${c[2]} "${c[0]}" to true.`, { code: 'INVALID_REQUEST' });
            }
        }
    }

    // delete unchanged values, convert key names to cognito attributes
    let toRemove = [];
    for (let k in params) {
        if (params[k] === this.user[k]) {
            toRemove.push(k);
            continue;
        }

        let customAttr = [
            'email_public',
            'phone_number_public',
            'address_public',
            'gender_public',
            'birthdate_public',
            'misc'
        ];

        if (customAttr.includes(k)) {
            let parseValue = params[k];

            if (typeof parseValue === 'boolean') {
                parseValue = parseValue ? '1' : '0';
            }

            params['custom:' + k] = parseValue;
            toRemove.push(k);
        }
    }

    for (let k of toRemove) {
        delete params[k];
    }

    if (params && typeof params === 'object' && Object.keys(params).length) {
        // format params to cognito attribute
        let toSet: Array<CognitoUserAttribute> = [];
        for (let key in params) {
            toSet.push(new CognitoUserAttribute({
                Name: key,
                Value: params[key]
            }));
        }

        await new Promise((res, rej) => {
            cognitoUser?.updateAttributes(
                toSet,
                (err: any, result: any) => {
                    if (err) {
                        rej(
                            [
                                err?.code || err?.name,
                                err?.message || `Failed to update user settings.`
                            ]
                        );
                    }
                    res(result);
                });
        });

        await authentication.bind(this)().getSession({ refreshToken: true });
        return this.user;
    }

    return this.user;
}

export async function getUsers(
    params?: {
        searchFor: string;
        value: string | number | boolean;
        condition?: '>' | '>=' | '=' | '<' | '<=' | '!=' | 'gt' | 'gte' | 'eq' | 'lt' | 'lte' | 'ne';
        range?: string | number | boolean;
    },
    fetchOptions?: FetchOptions): Promise<DatabaseResponse<PublicUser>> {
    if (!params) {
        // set default value
        params = {
            searchFor: 'timestamp',
            condition: '>',
            value: 0
        };

        if (!fetchOptions) {
            fetchOptions = {};
        }

        fetchOptions.ascending = false;
    }

    await this.__connection;

    const searchForTypes = {
        'user_id': (v: string) => validator.UserId(v),
        'email': (v: string) => validator.Email(v),
        'phone_number': (v: string) => validator.PhoneNumber(v),
        'locale': (v: string) => {
            if (typeof v !== 'string' || typeof v === 'string' && v.length > 5) {
                throw new SkapiError('Value of "locale" should be a country code.', { code: 'INVALID_PARAMETER' });
            }
            return v;
        },
        'name': 'string',
        'address': 'string',
        'gender': 'string',
        'birthdate': (v: string) => validator.Birthdate(v),
        'subscribers': 'number',
        'timestamp': 'number',
        'access_group': 'number',
        'approved': (v: boolean) => {
            if (v) {
                return 'by_admin:approved';
            }
            else {
                return 'by_admin:suspended';
            }
        }
    };

    let required = ['searchFor', 'value'];

    params = validator.Params(params, {
        searchFor: [
            'user_id',
            'name',
            'email',
            'phone_number',
            'address',
            'gender',
            'birthdate',
            'locale',
            'subscribers',
            'timestamp',
            'access_group',
            'approved'
        ],
        condition: ['>', '>=', '=', '<', '<=', 'gt', 'gte', 'eq', 'lt', 'lte', () => '='],
        value: (v: any) => {
            let checker = searchForTypes[params.searchFor];
            if (typeof checker === 'function') {
                return checker(v);
            }

            else if (typeof v !== checker) {
                throw new SkapiError(`Value does not match the type of "${params.searchFor}" index.`, { code: 'INVALID_PARAMETER' });
            }

            return v;
        },
        range: (v: any) => {
            let checker = searchForTypes[params.searchFor];
            if (typeof checker === 'function') {
                return checker(v);
            }

            else if (typeof v !== checker) {
                throw new SkapiError(`Range does not match the type of "${params.searchFor}" index.`, { code: 'INVALID_PARAMETER' });
            }

            return v;
        }
    }, required);

    if (params?.condition && params?.condition !== '=' && params.hasOwnProperty('range')) {
        throw new SkapiError('Conditions does not apply on range search.', { code: 'INVALID_PARAMETER' });
    }

    if (['user_id', 'phone_number', 'email'].includes(params.searchFor) && (params.condition !== '=' || params.range)) {
        throw new SkapiError(`Conditions are not allowed on "${params.searchFor}"`, { code: 'INVALID_PARAMETER' });
    }

    if (typeof params?.value === 'string' && !params?.value) {
        throw new SkapiError('Value should not be an empty string.', { code: 'INVALID_PARAMETER' });
    }

    if (typeof params?.searchFor === 'string' && !params?.searchFor) {
        throw new SkapiError('"searchFor" should not be an empty string.', { code: 'INVALID_PARAMETER' });
    }

    return request.bind(this)('get-users', params, { auth: true, fetchOptions });
}

/**
 * Not official. Bleeding edge.<br>
 * Retrieves, reverts e-mail to last verified email.<br>
 * @returns Last verified e-mail address, or updated userProfile when params.revert is true.
 */
export async function lastVerifiedEmail(params?: {
    /** Reverts to last verified e-mail when true. */
    revert: boolean;
}): Promise<string | UserProfile> {
    await this.__connection;
    let res = await request.bind(this)('last-verified-email', params, { auth: true });
    if (res.includes('SUCCESS')) {
        await authentication.bind(this)().getSession({ refreshToken: true });
        return this.user;
    }
    return res;
}

/**
 * Not official. Bleeding edge.<br>
 * Requests username(e-mail) change.<br>
 * skapi server will send username change confirmation e-mail to user.<br>
 * Username will not be changed when the user did not confirm.<br>
 * Confirmation e-mail is valid within 24 hours.
 */
export async function requestUsernameChange(params: {
    /** Redirect URL when user clicks on the link. */
    redirect?: string;
    /** username(e-mail) user wish to change to. */
    username: string;
}): Promise<'SUCCESS: confirmation e-mail has been sent.'> {
    await this.__connection;

    params = validator.Params(params, {
        username: validator.Email,
        redirect: validator.Url
    }, ['username']);

    return await request.bind(this)('request-username-change', params, { auth: true });
}

import {
    RecordData,
    Form,
    FetchOptions,
    DatabaseResponse,
    GetRecordQuery,
    Condition,
    PostRecordConfig,
    ProgressCallback,
    BinaryFile
} from '../Types';
import SkapiError from '../main/error';
import { extractFormData, fromBase62 } from '../utils/utils';
import validator from '../utils/validator';
import { request, uploadFiles } from '../utils/network';
import { checkAdmin } from './user';

const __index_number_range = 4503599627370496; // +/-

export async function normalizeRecord(record: Record<string, any>): Promise<RecordData> {
    const output: Record<string, any> = {
        user_id: '',
        record_id: '',
        updated: 0,
        uploaded: 0,
        table: {
            name: '',
            access_group: 0
        },
        reference: {
            reference_limit: null,
            allow_multiple_reference: true,
            referenced_count: 0
        },
        ip: '',
        bin: {}
    };

    const keys = {
        'ip': (r: string) => {
            if (r.slice(-1) === 'R') {
                output.readonly = true;
                r = r.slice(0, -1);
            }
            else {
                output.readonly = false;
            }
            output.ip = r;
        },
        'rec': (r: string) => {
            if (!r) return;
            output.record_id = r;
            let base62timestamp = r.substring(0, r.length - 9); // id: [base62 timestamp][random 5 char][suid 4 char]
            let uploaded = fromBase62(base62timestamp);
            output.uploaded = uploaded;
        },
        'usr': (r: string) => {
            output.user_id = r;
        },
        'tbl': (r: string) => {
            if (!r) return;
            // table/service/group(** | group)/[subscription(user id)/group(00 - 99)]/[tag]
            if (!output.table.name) {
                let rSplit = r.split('/');
                output.table.name = rSplit[0];
                let access_group = rSplit[2] == '**' ? 'private' : parseInt(rSplit[2]);
                access_group = access_group == 0 ? 'public' : access_group == 1 ? 'authorized' : access_group;
                output.table.access_group = access_group;
                // if (rSplit?.[3]) {
                //     output.table.subscription = {
                //         user_id: rSplit[3],
                //         group: parseInt(rSplit[4])
                //     };
                // }
                if (rSplit?.[3]) {
                    output.table.subscription = true;
                }
                else {
                    output.table.subscription = false;
                }
            }
        },
        'usr_tbl': (r: string) => {
            // user-id/table/service/group(** | group)[/subscription(user id)/group(00 - 99)][/tag]
            let rSplit = r.split('/');
            if (!output.user_id) {
                output.user_id = rSplit[0];
            }
            if (!output.table.name) {
                output.table.name = rSplit[1];
                let access_group = rSplit[3] == '**' ? 'private' : parseInt(rSplit[3]);
                access_group = access_group == 0 ? 'public' : access_group == 1 ? 'authorized' : access_group;
                output.table.access_group = access_group;
                // if (rSplit?.[4]) {
                //     output.table.subscription = {
                //         user_id: rSplit[4],
                //         group: parseInt(rSplit[5])
                //     };
                // }
                if (rSplit?.[4]) {
                    output.table.subscription = true;
                }
                else {
                    output.table.subscription = false;
                }
            }
        },
        'idx': (r: string) => {
            if (!r) return;
            let rSplit = r.split('!');
            let name = rSplit.splice(0, 1)[0];
            let value = normalizeTypedString('!' + rSplit.join('!'));
            output.index = {
                name,
                value
            };
        },
        'ref': (r: string) => {
            if (!r) return;
            output.reference.record_id = r.split('/')[0];
        },
        'tags': (r: string[]) => {
            output.tags = r;
        },
        'upd': (r: number) => {
            output.updated = r;
        },
        'acpt_mrf': (r: boolean) => {
            output.reference.allow_multiple_reference = r;
        },
        'ref_limt': (r: number) => {
            output.reference.reference_limit = r;
        },
        'rfd': (r: number) => {
            output.reference.referenced_count = r;
        },
        'bin': async (r: string[]) => {
            let binObj = {};
            if (Array.isArray(r)) {
                for (let url of r) {
                    let path = url.split('/').slice(3).join('/');
                    let splitPath = path.split('/');
                    let filename = decodeURIComponent(splitPath.slice(-1)[0]);
                    let pathKey = decodeURIComponent(splitPath[10]);
                    let size = splitPath[9];
                    let uploaded = splitPath[8];
                    let access_group = splitPath[6] == '**' ? 'private' : parseInt(splitPath[6]);
                    access_group = access_group == 0 ? 'public' : access_group == 1 ? 'authorized' : access_group;

                    let url_endpoint = url;
                    if (access_group !== 'public') {
                        if(access_group === 'private' && this.__user?.user_id !== splitPath[3]) {
                            url_endpoint = url;
                        }
                        else {
                            url_endpoint = (await getFile.bind(this)(url, { dataType: 'endpoint' }) as string);
                        }
                    }

                    let obj = {
                        access_group,
                        filename,
                        url: url_endpoint,
                        path,
                        size: fromBase62(size),
                        uploaded: fromBase62(uploaded),
                        getFile: (dataType: 'base64' | 'endpoint' | 'blob' | 'download', progress?: ProgressCallback) => {
                            let config = {
                                dataType: dataType || 'download',
                                progress
                            };
                            return getFile.bind(this)(url, config);
                        }
                    };
                    if (binObj[pathKey]) {
                        binObj[pathKey].push(obj);
                        continue;
                    }

                    binObj[pathKey] = [obj];
                }
            }
            output.bin = binObj;
        },
        'data': (r: any) => {
            let data = r;
            if (r === '!D%{}') {
                data = {};
            }
            else if (r === '!L%[]') {
                data = [];
            }
            output.data = data;
        }
    };

    if (record.record_id) {
        // bypass already normalized records
        return record as RecordData;
    }

    for (let k in keys) {
        if (record.hasOwnProperty(k)) {
            let exec = keys[k](record[k]);
            if (exec instanceof Promise) {
                await exec;
            }
        }
    }

    if (record.private_key) {
        this.__private_access_key[output.record_id] = record.private_key;
    }

    return output as RecordData;
}

function normalizeTypedString(v: string) {
    let value = v.substring(3);
    let type = v.substring(0, 3);

    switch (type) {
        case "!S%":
            // !S%string
            return value;
        case "!N%":
            // !N%0
            let splitDec = value.split('.');
            let calcNumb = Number(splitDec[0]) - 4503599627370496;
            if (splitDec.length === 1) {
                return calcNumb;
            }
            return parseFloat(calcNumb.toString() + '.' + splitDec[1]);
        case "!B%":
            // !B%1
            return value === '1';
        case "!L%":
        case "!D%":
            // !L%[0, "hello"] / !D%{}
            try {
                return JSON.parse(value);
            } catch (err) {
                throw new SkapiError('Value parse error.', { code: 'PARSE_ERROR' });
            }
        default:
            return v;
    }
}

export async function deleteFiles(params: {
    endpoints: string | string[], // file endpoints
}): Promise<RecordData[]> {
    let { endpoints } = params;

    if (typeof endpoints === 'string') {
        endpoints = [endpoints];
    }

    if (!Array.isArray(endpoints)) {
        throw new SkapiError('"endpoints" should be type: array | string.', { code: 'INVALID_PARAMETER' });
    }

    let updatedRec = await request.bind(this)('del-files', {
        endpoints,
        storage: 'records'
    }, { auth: true, method: 'post' });

    for (let i in updatedRec) {
        updatedRec[i] = normalizeRecord.bind(this)(updatedRec[i]);
    }

    return await Promise.all(updatedRec);
}

export async function getFile(
    url: string, // cdn endpoint url https://xxxx.cloudfront.net/path/file
    config?: {
        dataType?: 'base64' | 'download' | 'endpoint' | 'blob' | 'text'; // default 'download'
        expires?: number; // uses url that expires. this option does not use the cdn (slow). can be used for private files. (does not work on public files).
        progress?: ProgressCallback;
    }
): Promise<Blob | string | void> {
    if (typeof url !== 'string') {
        throw new SkapiError('"url" should be type: string.', { code: 'INVALID_PARAMETER' });
    }

    validator.Url(url);
    let isValidEndpoint = false;
    let splitUrl = url.split('/');
    let host = splitUrl[2];
    let splitHost = host.split('.');
    let subdomain = null;

    if (splitHost.length === 3 && splitHost[1] === 'skapi') {
        subdomain = splitHost[0];
        isValidEndpoint = true;
    }

    let target_key = splitUrl.slice(3);
    let needAuth = false;
    if (!isValidEndpoint) {
        if (target_key[0] === 'auth' || target_key[0] === 'publ') {
            try {
                validator.UserId(target_key[2]);
                validator.UserId(target_key[3]);
                needAuth = target_key[0] == 'auth';
                isValidEndpoint = true;
            }
            catch {
                throw new SkapiError('Invalid file url.', { code: 'INVALID_PARAMETER' });
            }
        }
    }

    let service = subdomain ? null : target_key[1];

    config = validator.Params(config, {
        expires: ['number', () => 0],
        dataType: ['base64', 'blob', 'endpoint', 'text', () => 'download'],
        progress: 'function'
    });

    let filename = url.split('/').slice(-1)[0];
    let expires = config.expires;

    if (expires) {
        if(!isValidEndpoint) {
            throw new SkapiError('Expires option can only be used on skapi cdn endpoints.', { code: 'INVALID_PARAMETER' });
        }

        if (expires < 0) {
            throw new SkapiError('"config.expires" should be > 0. (seconds)', { code: 'INVALID_PARAMETER' });
        }

        let params: Record<string, any> = {
            request: subdomain ? 'get-host' : 'get',
            id: subdomain || target_key[5],
            key: url,
            expires
        }

        if (service) {
            params.service = service
        }

        url = (await request.bind(this)('get-signed-url', params,
            { auth: true }
        )).url;
    }

    else if (needAuth) {
        let token = this.session?.idToken?.jwtToken; // idToken

        let access_group = target_key[6] === '**' ? '**' : parseInt(target_key[6]);

        if (!token) {
            throw new SkapiError('User login is required.', { code: 'INVALID_REQUEST' });
        }
        else {
            let currTime = Date.now() / 1000;
            if (this.session.idToken.payload.exp < currTime) {
                try {
                    await this.authentication().getSession({ refreshToken: true });
                    token = this.session?.idToken?.jwtToken;
                }
                catch (err) {
                    this.logout();
                    throw new SkapiError('User login is required.', { code: 'INVALID_REQUEST' });
                }
            }
        }

        if (access_group === '**') {
            if (this.__user.user_id !== target_key[3]) {
                throw new SkapiError('User has no access.', { code: 'INVALID_REQUEST' });
            }
        }
        else if (this.__user.access_group < access_group) {
            throw new SkapiError('User has no access.', { code: 'INVALID_REQUEST' });
        }

        url += `?t=${token}`;
    }

    if (config?.dataType === 'endpoint') {
        return url;
    }

    if (config?.dataType === 'download') {
        let a = document.createElement('a');
        // Set the href attribute to the file URL
        a.href = url;
        document.body.appendChild(a);
        a.setAttribute('download', filename);
        a.target = '_blank';
        a.click();
        document.body.removeChild(a);
        return null;
    }

    let blob: Promise<string> = new Promise(async (res, rej) => {
        try {
            let b = await request.bind(this)(
                url,
                null,
                { method: 'get', contentType: null, responseType: config?.dataType === 'text' ? 'text' : 'blob', fetchOptions: { progress: config?.progress } },
                { ignoreService: true }
            );
            if (config?.dataType === 'base64') {
                const reader = new FileReader();
                reader.onloadend = () => res((reader.result as string));
                reader.readAsDataURL(b);
            }
            else {
                res(b);
            }
        } catch (err) {
            rej(err);
        }
    });

    return blob;
}

export async function getRecords(query: GetRecordQuery & { private_key?: string; }, fetchOptions?: FetchOptions): Promise<DatabaseResponse<RecordData>> {
    await this.__connection;

    if (typeof query?.table === 'string') {
        query.table = {
            name: query.table,
            access_group: 0
        };
    }

    let is_reference_fetch = '';
    let ref_user = '';

    if (query?.record_id) {
        validator.specialChars(query.record_id, 'record_id', false, false);
        let outputObj: Record<string, string> = { record_id: query.record_id };
        if ((query as any)?.service) {
            outputObj.service = (query as any).service;
        }
        query = outputObj;
        if (this.__private_access_key[query.record_id]) {
            query.private_key = this.__private_access_key[query.record_id];
        }
    }

    else {
        const struct = {
            table: {
                name: [v => {
                    if (!v) {
                        throw new SkapiError('"table.name" cannot be empty string.', { code: 'INVALID_PARAMETER' });
                    }
                    return validator.specialChars(v, 'table name', true, true)
                }],
                access_group: [v => {
                    if (v === undefined) {
                        // access_group defaults to 1 if subscription value is present, else 0
                        if (!this.__user && query.table.hasOwnProperty('subscription')) {
                            return 1;
                        }
                        else {
                            return 0;
                        }
                    }

                    if (typeof v === 'number') {
                        if ((this.__user?.access_group || 0) < v) {
                            throw new SkapiError("User has no access", { code: 'INVALID_REQUEST' });
                        }
                    }
                    else if (typeof v === 'string') {
                        v = {
                            private: 'private',
                            public: 0,
                            authorized: 1
                        }[v]

                        if (v === 'private' && !this.__user) {
                            throw new SkapiError('Unsigned users have no access to private records.', { code: 'INVALID_REQUEST' });
                        }

                        if (v === undefined) {
                            throw new SkapiError('"table.access_group" is invalid.', { code: 'INVALID_PARAMETER' });
                        }
                    }
                    else {
                        throw new SkapiError('"table.access_group" should be type: <number | string>.', { code: 'INVALID_PARAMETER' });
                    }

                    return v;
                }],
                subscription: (v: string) => {
                    if (v === null || v === undefined) {
                        return v;
                    }
                    validator.UserId(v, 'User ID in "subscription"')
                    if (!this.__user) {
                        throw new SkapiError('Unsigned users have no access to subscription records.', { code: 'INVALID_REQUEST' });
                    }
                    return {
                        user_id: v,
                        group: 1
                    }
                }
            },
            reference: v => {
                if (v === null || v === undefined) {
                    return v;
                }
                if (typeof v === 'string') {
                    try {
                        ref_user = validator.UserId(v);
                    }
                    catch (err) {
                        // reference is record id
                        validator.specialChars(v, 'reference', false, false);
                        is_reference_fetch = v;
                        if (this.__private_access_key[is_reference_fetch]) {
                            query.private_key = this.__private_access_key[is_reference_fetch];
                        }
                    }

                    is_reference_fetch = v;
                    return v;
                }
                else {
                    throw new SkapiError('"reference" should be type: string.', { code: 'INVALID_PARAMETER' });
                }
            },
            index: {
                name: ['$updated', '$uploaded', '$referenced_count', '$user_id', (v: string) => {
                    if (v === undefined) {
                        throw new SkapiError('"index.name" is required.', { code: 'INVALID_PARAMETER' });
                    }
                    if (typeof v !== 'string') {
                        throw new SkapiError('"index.name" should be type: string.', { code: 'INVALID_PARAMETER' });
                    }

                    return validator.specialChars(v, 'index.name', true, false);
                }],
                value: (v: number | boolean | string) => {
                    const indexTypes = {
                        '$updated': 'number',
                        '$uploaded': 'number',
                        '$referenced_count': 'number',
                        '$user_id': validator.UserId
                    };

                    if (indexTypes.hasOwnProperty(query.index.name)) {
                        let tp = indexTypes[query.index.name];

                        if (typeof tp === 'function') {
                            return tp(v);
                        }

                        if (tp !== typeof v) {
                            throw new SkapiError(`"index.value" should be type: ${tp}.`, { code: 'INVALID_PARAMETER' });
                        }

                        return v;
                    }

                    else if (typeof v === 'number') {
                        if (v > __index_number_range || v < -__index_number_range) {
                            throw new SkapiError(`Number value should be within range -${__index_number_range} ~ +${__index_number_range}`, { code: 'INVALID_PARAMETER' });
                        }
                        return v;
                    }

                    else if (typeof v === 'boolean') {
                        return v;
                    }

                    else if (typeof v === 'string') {
                        return v;
                    }

                    else {
                        throw new SkapiError(`"index.value" should be type: <number | boolean | string>.`, { code: 'INVALID_PARAMETER' });
                    }
                },
                condition: ['gt', 'gte', 'lt', 'lte', '>', '>=', '<', '<=', '=', 'eq', '!=', 'ne'],
                range: (v: number | boolean | string) => {
                    if (!query.index || !('value' in query.index)) {
                        throw new SkapiError('"index.value" is required.', { code: 'INVALID_PARAMETER' });
                    }

                    if (query.index.name === '$record_id') {
                        throw new SkapiError(`Cannot do "index.range" on ${query.index.name}`, { code: 'INVALID_PARAMETER' });
                    }

                    if (typeof query.index.value !== typeof v) {
                        throw new SkapiError('"index.range" type should match the type of "index.value".', { code: 'INVALID_PARAMETER' });
                    }

                    if (typeof v === 'string') {
                        return validator.specialChars(v, 'index.range', false, true);
                    }

                    return v;
                }
            },
            tag: (v: string) => {
                if (v === null || v === undefined) {
                    return v;
                }
                if (typeof v === 'string') {
                    return validator.specialChars(v, 'tag', false, true)
                }
                else {
                    throw new SkapiError('"tag" should be type: string.', { code: 'INVALID_PARAMETER' });
                }
            },
            private_key: 'string'
        };

        let isAdmin = await checkAdmin.bind(this)();
        query = validator.Params(query || {}, struct, ref_user || isAdmin ? [] : ['table']);
    }

    let result = await request.bind(this)(
        'get-records',
        query,
        {
            fetchOptions,
            auth: !!this.__user,
            method: !!this.__user ? 'post' : 'get'
        }
    );

    for (let i in result.list) {
        result.list[i] = normalizeRecord.bind(this)(result.list[i]);
    };

    result.list = await Promise.all(result.list);

    if (is_reference_fetch && result?.reference_private_key) {
        this.__private_access_key[is_reference_fetch] = result.reference_private_key;
    }
    return result;
}

export async function postRecord(
    form: Form<Record<string, any>> | null | undefined,
    config: PostRecordConfig & { progress?: ProgressCallback; reference_private_key?: string; }
): Promise<RecordData> {
    let isAdmin = await checkAdmin.bind(this)();
    if (!config) {
        throw new SkapiError('"config" argument is required.', { code: 'INVALID_PARAMETER' });
    }

    if (!this.user) {
        throw new SkapiError('Login is required.', { code: 'INVALID_REQUEST' });
    }

    if (typeof config.table === 'string') {
        config.table = {
            name: config.table
        };

        if (!config.record_id) {
            config.table.access_group = 0;
        }
    }

    if (typeof config.reference === 'string') {
        config.reference = {
            record_id: config.reference
        }
    }
    let _config = validator.Params(config || {}, {
        record_id: ['string', () => {
            if (!config.table || !config.table.name) {
                throw new SkapiError('"table.name" is required.', { code: 'INVALID_PARAMETER' });
            }
        }],
        readonly: 'boolean',
        table: {
            name: v => {
                if (!v) {
                    throw new SkapiError('"table.name" cannot be empty string.', { code: 'INVALID_PARAMETER' });
                }
                return validator.specialChars(v, 'table name', true, true)
            },
            // subscription_group: ['number', null],
            subscription: v => {
                if (v) {
                    if (!config.record_id && !config.table.access_group || config.table.access_group === 0 || config.table.access_group === 'public') {
                        throw new SkapiError('Public records cannot require subscription.', { code: 'INVALID_REQUEST' });
                    }
                }

                return v;
            },
            access_group: v => {
                if (typeof v === 'string') {
                    v = {
                        private: 'private',
                        public: 0,
                        authorized: 1
                    }[v]
                }

                if (typeof v === 'number') {
                    if (!isAdmin && this.user.access_group < v) {
                        throw new SkapiError("User has no access", { code: 'INVALID_REQUEST' });
                    }
                }

                if (v === undefined) {
                    throw new SkapiError('"table.access_group" is invalid.', { code: 'INVALID_PARAMETER' });
                }

                return v;
            }
        },
        reference: {
            record_id: v => {
                validator.specialChars(v, '"reference.record_id"', false, false);
                if (this.__private_access_key[v]) {
                    config.reference_private_key = this.__private_access_key[v];
                }
                return v;
            },
            reference_limit: (v: number) => {
                if (v === null) {
                    return null;
                }

                else if (typeof v === 'number') {
                    if (0 > v) {
                        throw new SkapiError(`"reference_limit" should be >= 0`, { code: 'INVALID_PARAMETER' });
                    }

                    if (v > 4503599627370546) {
                        throw new SkapiError(`"reference_limit" should be <= 4503599627370546`, { code: 'INVALID_PARAMETER' });
                    }

                    return v;
                }

                throw new SkapiError(`"reference_limit" should be type: <number | null>`, { code: 'INVALID_PARAMETER' });
            },
            allow_multiple_reference: 'boolean',
        },
        index: {
            name: ['$uploaded', '$updated', '$referenced_count', '$user_id', v => {
                if (!v) {
                    throw new SkapiError('"index.name" is required.', { code: 'INVALID_PARAMETER' });
                }
                if (typeof v === 'string') {
                    validator.specialChars(v, 'index name', true);
                    return v;
                }
                throw new SkapiError('"index.name" should be type: string.', { code: 'INVALID_PARAMETER' });
            }],
            value: [v => {
                if (!v && typeof v !== 'boolean' && v !== 0) {
                    throw new SkapiError('"index.value" is required.', { code: 'INVALID_PARAMETER' });
                }

                let availTypes = ['boolean', 'string', 'number'];
                if (!availTypes.includes(typeof v)) {
                    throw new SkapiError('"index.value" should be type: <boolean | string | number>.', { code: 'INVALID_PARAMETER' });
                }

                if (typeof v === 'string') {
                    validator.specialChars(v, 'index value', false, true);
                }

                else if (typeof v === 'number') {
                    if (v > __index_number_range || v < -__index_number_range) {
                        throw new SkapiError(`Number value should be within range -${__index_number_range} ~ +${__index_number_range}`, { code: 'INVALID_PARAMETER' });
                    }
                }

                return v;
            }]
        },
        tags: (v: string | string[]) => {
            if (v === null || v === undefined) {
                return v;
            }
            if (typeof v === 'string') {
                v = v.split(',').map(t => t.trim());
            }
            return validator.specialChars(v, 'tag', false, true);
        },
        remove_bin: (v: string[] | BinaryFile[] | null) => {
            if (!v) {
                return null;
            }

            let arr = []
            if (Array.isArray(v)) {
                for (let i of v) {
                    if (typeof i === 'string') {
                        arr.push(i);
                    }
                    else if (i.url && i.size && i.filename) {
                        arr.push(i.url);
                    }
                    else {
                        throw new SkapiError(`"remove_bin" should be type: <string[] | BinaryFile[]>`, { code: 'INVALID_PARAMETER' });
                    }
                }
            }
            else {
                throw new SkapiError(`"remove_bin" should be type: <string[] | BinaryFile[]>`, { code: 'INVALID_PARAMETER' });
            }

            return arr;
        },
        progress: 'function',
    });
    let progress = config.progress || null;
    if (config.table && config.table.hasOwnProperty('subscription')) {
        _config.table.subscription_group = config.table.subscription ? 1 : null;
        delete _config.table.subscription;
    }
    // callbacks should be removed after checkparams
    delete _config.progress;

    let options: { [key: string]: any } = { auth: true };
    let postData = null;
    let to_bin = null;
    let extractedForm = extractFormData(form);

    if (extractedForm.files.length) {
        to_bin = extractedForm.files;
    }

    postData = Object.assign({ data: extractedForm.data }, _config);

    let fetchOptions: { [key: string]: any } = {};

    if (typeof progress === 'function') {
        fetchOptions.progress = progress;
    }

    if (Object.keys(fetchOptions).length) {
        Object.assign(options, { fetchOptions });
    }
    let rec = await request.bind(this)('post-record', postData, options);
    if (to_bin) {
        let bin_formData = new FormData();
        for (let f of to_bin) {
            bin_formData.append(f.name, f.file, f.file.name);
        }
        let uploadFileParams = {
            record_id: rec.rec,
            progress
        }
        if (_config.hasOwnProperty('service')) {
            uploadFileParams['service'] = _config.service;
        }
        let { bin_endpoints } = await uploadFiles.bind(this)(bin_formData, uploadFileParams);
        if (!rec.bin) {
            rec.bin = bin_endpoints;
        }
        else {
            rec.bin.push(...bin_endpoints);
        }
    }

    return normalizeRecord.bind(this)(rec);
}

export async function getTables(
    /** If null fetch all list of tables. */
    query: {
        table: string;
        /** Condition operator of table name. */
        condition?: Condition;
    },
    fetchOptions?: FetchOptions
): Promise<DatabaseResponse<{
    number_of_records: number; // Number of records in the table
    table: string; // Table name
    size: number; // Table size
}>> {
    let res = await request.bind(this)('get-table', validator.Params(query || {}, {
        table: 'string',
        condition: ['gt', 'gte', 'lt', 'lte', '>', '>=', '<', '<=', '=', 'eq', '!=', 'ne']
    }), Object.assign({ auth: !!this.__user }, { fetchOptions }));

    let convert = {
        'cnt_rec': 'number_of_records',
        'tbl': 'table',
        'srvc': 'service'
    };

    if (Array.isArray(res?.list)) {
        for (let t of res.list) {
            for (let k in convert) {
                if (t.hasOwnProperty(k)) {
                    t[convert[k]] = t[k];
                    delete t[k];
                }
            }
        }
    }

    return res;
}

export async function getIndexes(
    query: {
        /** Table name */
        table: string;
        /** Index name. When period is at the end of name, querys nested index keys. */
        index?: string;
        /** Queries order by */
        order?: {
            /** Key name to order by. */
            by: 'average_number' | 'total_number' | 'number_count' | 'average_bool' | 'total_bool' | 'bool_count' | 'string_count' | 'index_name';
            /** Value to query. */
            value?: number | boolean | string;
            condition?: Condition;
        };
    },
    fetchOptions?: FetchOptions
): Promise<DatabaseResponse<{
    table: string; // Table name
    index: string; // Index name
    number_of_records: number; // Number of records in the index
    string_count?: number; // Number of string type value
    number_count?: number; // Number of number type value
    boolean_count?: number; // Number of boolean type value
    total_number?: number; // Sum of all numbers
    total_bool?: number; // Number of true(boolean) values
    average_number?: number; // Average of all numbers
    average_bool?: number; // Percentage of true(boolean) values
}>> {
    if (!query?.table) {
        throw new SkapiError('"table" is required.', { code: 'INVALID_PARAMETER' });
    }

    let p = validator.Params(
        query || {},
        {
            table: 'string',
            index: (v: string) => validator.specialChars(v, 'index name', true, false),
            order: {
                by: [
                    'average_number',
                    'total_number',
                    'number_count',
                    'average_bool',
                    'total_bool',
                    'bool_count',
                    'string_count',
                    'index_name',
                    'number_of_records'
                ],
                value: ['string', 'number', 'boolean'],
                condition: ['gt', 'gte', 'lt', 'lte', '>', '>=', '<', '<=', '=', 'eq', '!=', 'ne']
            }
        },
        ['table']
    );

    if (p.hasOwnProperty('order')) {
        if (!p.order?.by) {
            throw new SkapiError('"order.by" is required.', { code: 'INVALID_PARAMETER' });
        }

        if (p.order.hasOwnProperty('condition') && !p.order.hasOwnProperty('value')) {
            throw new SkapiError('"value" is required for "condition".', { code: 'INVALID_PARAMETER' });
        }

        if (p.hasOwnProperty('index')) {
            if (p.index.substring(p.index.length - 1) !== '.') {
                throw new SkapiError('"index" should be a parent index name of the compound index when using "order.by"', { code: 'INVALID_PARAMETER' });
            }
        }
    }

    let res = await request.bind(this)(
        'get-index',
        p,
        Object.assign(
            { auth: !!this.__user },
            { fetchOptions }
        )
    );

    let convert = {
        'cnt_bool': 'boolean_count',
        'cnt_numb': 'number_count',
        'totl_numb': 'total_number',
        'totl_bool': 'total_bool',
        'avrg_numb': 'average_number',
        'avrg_bool': 'average_bool',
        'cnt_str': 'string_count'
    };

    if (Array.isArray(res?.list)) {
        res.list = res.list.map((i: Record<string, any>) => {
            let iSplit = i.idx.split('/');
            let resolved: Record<string, any> = {
                table: iSplit[1],
                index: iSplit[2],
                number_of_records: i.cnt_rec
            };

            for (let k in convert) {
                if (i?.[k]) {
                    resolved[convert[k]] = i[k];
                }
            }

            return resolved;
        });
    }

    return res;
}

export async function getTags(
    query?: {
        /** Table name */
        table: string;
        /** Tag name */
        tag?: string;
        /** String query condition for tag name. */
        condition?: Condition;
    },
    fetchOptions?: FetchOptions
): Promise<DatabaseResponse<{
    table: string; // Table name
    tag: string; // Tag
    number_of_records: string; // Number records tagged
}>> {

    let res = await request.bind(this)(
        'get-tag',
        validator.Params(query || {},
            {
                table: 'string',
                tag: 'string',
                condition: ['gt', 'gte', 'lt', 'lte', '>', '>=', '<', '<=', '=', 'eq', '!=', 'ne']
            }
        ),
        Object.assign({ auth: !!this.__user }, { fetchOptions })
    );

    if (Array.isArray(res?.list)) {
        for (let i in res.list) {
            let item = res.list[i];
            let tSplit = item.tag.split('/');
            res.list[i] = {
                table: tSplit[1],
                tag: tSplit[0],
                number_of_records: item.cnt_rec
            };
        }
    }

    return res;
}

export async function deleteRecords(params: {
    /** @ignore */
    service?: string;
    /** Record ID(s) to delete. Table parameter is not needed when record_id is given. */
    record_id?: string | string[];
    table?: {
        /** Table name. */
        name: string;
        /** Access group number. */
        access_group?: number | 'private' | 'public' | 'authorized';
        subscription?: boolean;
        // /** @ignore */
        // subscription?: string;
        // subscription_group?: number;
    };
}): Promise<string> {
    let isAdmin = await checkAdmin.bind(this)();
    if (isAdmin && !params?.service) {
        throw new SkapiError('Service ID is required.', { code: 'INVALID_PARAMETER' });
    }

    if (params?.record_id) {
        return await request.bind(this)('del-records', {
            service: params.service || this.service,
            record_id: (id => {
                if (typeof id === 'string') {
                    return [id];
                }

                if (!Array.isArray(id)) {
                    throw new SkapiError('"record_id" should be type: <string | string[]>', { code: 'INVALID_PARAMETER' });
                }

                if (id.length > 100) {
                    throw new SkapiError('"record_id" should not exceed 100 items.', { code: 'INVALID_PARAMETER' });
                }

                return validator.specialChars(id, 'record_id', false, false);

            })(params.record_id)
        }, { auth: true });
    }

    else {
        if (!params?.table) {
            throw new SkapiError('Either "table" or "record_id" is required.', { code: 'INVALID_PARAMETER' });
        }

        let struct = {
            access_group: (v: number | 'private' | 'public' | 'authorized') => {
                if (typeof v === 'string' && ['private', 'public', 'authorized'].includes(v)) {
                    switch (v) {
                        case 'private':
                            return v;

                        case 'public':
                            return 0;

                        case 'authorized':
                            return 1;
                    }
                }

                else if (typeof v === 'number' && v >= 0 && v < 100) {
                    // if (!isAdmin && this.user.access_group < v) {
                    //     throw new SkapiError("User has no access", { code: 'INVALID_REQUEST' });
                    // }

                    return v;
                }

                throw new SkapiError('Invalid "table.access_group". Access group should be type <number (0~99) | "private" | "public" | "authorized">.', { code: 'INVALID_PARAMETER' });
            },
            name: 'string',
            subscription: (v: string | boolean) => {
                if (typeof v === 'boolean') {
                    if (v) {
                        v = this.__user.user_id;
                        return v;
                    }
                }

                if (!params.table.hasOwnProperty('access_group')) {
                    throw new SkapiError('"table.access_group" is required for subscription records.', { code: 'INVALID_PARAMETER' });
                }
                else if (params.table.access_group === 0) {
                    throw new SkapiError('Public tables does not hold subscription records.', { code: 'INVALID_REQUEST' });
                }

                if (isAdmin && typeof v === 'string') {
                    // admin targets user id
                    return validator.UserId((v as string), 'User ID in "table.subscription"');
                }

                throw new SkapiError('"table.subscription" is an invalid parameter key.', { code: 'INVALID_PARAMETER' });
            }
        };

        let table = validator.Params(params.table || {}, struct, isAdmin ? [] : ['name']);
        if (table.subscription) {
            table.subscription_group = 1;
        }
        let toDelete = Object.assign({}, params, { table });
        return await request.bind(this)('del-records', toDelete, { auth: true });
    }
}

export function grantPrivateRecordAccess(params: {
    record_id: string;
    user_id: string | string[];
}) {
    if (!params.record_id) {
        throw new SkapiError(`Record ID is required.`, { code: 'INVALID_PARAMETER' });
    }

    if (!params.user_id || Array.isArray(params.user_id) && !params.user_id.length) {
        throw new SkapiError(`User ID is required.`, { code: 'INVALID_PARAMETER' });
    }

    return recordAccess.bind(this)({
        record_id: params.record_id,
        user_id: params.user_id || null,
        execute: 'add'
    });
}

export function removePrivateRecordAccess(params: {
    record_id: string;
    user_id: string | string[];
}) {
    if (!params.record_id) {
        throw new SkapiError(`Record ID is required.`, { code: 'INVALID_PARAMETER' });
    }

    if (!params.user_id || Array.isArray(params.user_id) && !params.user_id.length) {
        throw new SkapiError(`User ID is required.`, { code: 'INVALID_PARAMETER' });
    }

    return recordAccess.bind(this)({
        record_id: params.record_id,
        user_id: params.user_id || null,
        execute: 'remove'
    });
}

export async function listPrivateRecordAccess(params: {
    record_id: string;
    user_id: string | string[];
}): Promise<DatabaseResponse<{ record_id: string; user_id: string; }>> {
    let list = await recordAccess.bind(this)({
        record_id: params.record_id,
        user_id: params.user_id || null,
        execute: 'list'
    });

    list.list = list.list.map((i: Record<string, any>) => {
        i.record_id = i.rec_usr.split('/')[0];
        i.user_id = i.rec_usr.split('/')[1];
        return i;
    });

    return list;
}

export function requestPrivateRecordAccessKey(record_id: string) {
    return request.bind(this)(
        'request-private-access-key',
        { record_id },
        { auth: true }
    );
}

function recordAccess(params: {
    record_id: string;
    user_id: string | string[];
    execute: 'add' | 'remove' | 'list';
}): Promise<any> {
    let execute = params.execute;
    let req = validator.Params(params,
        {
            record_id: 'string',
            user_id: (v: string | string[]) => {
                if (!v) {
                    if (execute == 'list') {
                        return null;
                    }

                    throw new SkapiError(`User ID is required.`, { code: 'INVALID_PARAMETER' });
                }

                let id = v;
                if (typeof id === 'string') {
                    id = [id];
                }

                if (id.length > 100) {
                    throw new SkapiError(`Cannot process more than 100 users at once.`, { code: 'INVALID_REQUEST' });
                }

                for (let i of id) {
                    validator.UserId(i, 'User ID in "user_id"');
                }

                return id;
            },
            execute: ['add', 'remove', 'list']
        },
        [
            'execute',
            'record_id',
            'user_id'
        ]
    );

    if (!req.user_id) {
        req.user_id = null;
    }

    return request.bind(this)(
        'grant-private-access',
        req,
        { auth: true }
    );
}

import SkapiError from '../main/error';
import validator from '../utils/validator';
import { extractFormData } from '../utils/utils';
import { request } from '../utils/network';
import { DatabaseResponse, FetchOptions } from '../Types';

async function prepareWebsocket() {
    // Connect to the WebSocket server
    await this.getProfile();

    if (!this.session) {
        throw new SkapiError(`No access.`, { code: 'INVALID_REQUEST' });
    }

    let r = await this.record_endpoint;

    return new WebSocket(
        r.websocket_private + '?token=' + this.session.accessToken.jwtToken
    );
}

type RealtimeCallback = (rt: {
    type: 'message' | 'error' | 'success' | 'close' | 'notice' | 'private';
    message: any;
    sender?: string; // user_id of the sender
    sender_cid?: string; // scid of the sender
}) => void;

let reconnectAttempts = 0;

let __roomList = {}; // { group: { user_id: [connection_id, ...] } }
let __roomPending = {}; // { group: Promise }
let __keepAliveInterval = null;
export function connectRealtime(cb: RealtimeCallback, delay = 0): Promise<WebSocket> {
    if (typeof cb !== 'function') {
        throw new SkapiError(`Callback must be a function.`, { code: 'INVALID_REQUEST' });
    }

    if (reconnectAttempts || !(this.__socket instanceof Promise)) {
        this.__socket = new Promise(async resolve => {
            setTimeout(async () => {
                await this.__connection;

                let user = await this.getProfile();
                if (!user) {
                    throw new SkapiError(`No access.`, { code: 'INVALID_REQUEST' });
                }

                let socket: WebSocket = await prepareWebsocket.bind(this)();

                socket.onopen = () => {
                    reconnectAttempts = 0;
                    cb({ type: 'success', message: 'Connected to WebSocket server.' });

                    if (this.__socket_room) {
                        socket.send(JSON.stringify({
                            action: 'joinRoom',
                            rid: this.__socket_room,
                            token: this.session.accessToken.jwtToken
                        }));
                    }

                    // keep alive
                    __keepAliveInterval = setInterval(() => {
                        if (socket.readyState === 1) {
                            socket.send(JSON.stringify({
                                action: 'keepAlive',
                                token: this.session.accessToken.jwtToken
                            }));
                        }
                    }, 30000);

                    resolve(socket);
                };

                socket.onmessage = async (event) => {
                    let data = JSON.parse(decodeURI(event.data));
                    let type: 'message' | 'error' | 'success' | 'close' | 'notice' | 'private' = 'message';

                    if (data?.['#private']) {
                        type = 'private';
                    }

                    else if (data?.['#notice']) {
                        type = 'notice';
                    }

                    let msg: {
                        type: 'message' | 'error' | 'success' | 'close' | 'notice' | 'private';
                        message: any;
                        sender?: string;
                        sender_cid?: string;
                    } = { type, message: (data?.['#message'] || data?.['#private'] || data?.['#notice']) || null };

                    if (data?.['#user_id']) {
                        msg.sender = data['#user_id'];
                    }

                    if (data?.['#scid']) {
                        msg.sender_cid = data['#scid'];
                    }

                    if(type === 'notice') {
                        if(this.__socket_room && (msg.message.includes('has left the message group.') || msg.message.includes('has been disconnected.'))) {
                            if(__roomPending[this.__socket_room]) {
                                await __roomPending[this.__socket_room];
                            }

                            let user_id = msg.sender;
                            if(__roomList?.[this.__socket_room]?.[user_id]) {
                                __roomList[this.__socket_room][user_id] = __roomList[this.__socket_room][user_id].filter(v=>v!==msg.sender_cid);
                            }

                            if(__roomList?.[this.__socket_room]?.[user_id] && __roomList[this.__socket_room][user_id].length === 0) {
                                delete __roomList[this.__socket_room][user_id];
                            }

                            if(__roomList?.[this.__socket_room]?.[user_id]) {
                                return
                            }
                        }
                        else if(this.__socket_room && msg.message.includes('has joined the message group.')) {
                            if(__roomPending[this.__socket_room]) {
                                await __roomPending[this.__socket_room];
                            }

                            let user_id = msg.sender;
                            if(!__roomList?.[this.__socket_room]) {
                                __roomList[this.__socket_room] = {};
                            }
                            if(!__roomList[this.__socket_room][user_id]) {
                                __roomList[this.__socket_room][user_id] = [msg.sender_cid];
                            }
                            else {
                                if(!__roomList[this.__socket_room][user_id].includes(msg.sender_cid)) {
                                    __roomList[this.__socket_room][user_id].push(msg.sender_cid);
                                }
                                return;
                            }
                        }
                    }
                    cb(msg);
                };

                socket.onclose = event => {
                    if (event.wasClean) {
                        // remove keep alive
                        clearInterval(__keepAliveInterval);
                        __keepAliveInterval = null;

                        cb({ type: 'close', message: 'WebSocket connection closed.' });
                        this.__socket = null;
                        this.__socket_room = null;
                    }
                    else {
                        // close event was unexpected
                        const maxAttempts = 10;
                        reconnectAttempts++;

                        if (reconnectAttempts < maxAttempts) {
                            let delay = Math.min(1000 * (2 ** reconnectAttempts), 30000); // max delay is 30 seconds
                            cb({ type: 'error', message: `Skapi: WebSocket connection error. Reconnecting in ${delay / 1000} seconds...` });
                            connectRealtime.bind(this)(cb, delay);
                        } else {
                            // Handle max reconnection attempts reached
                            cb({ type: 'error', message: 'Skapi: WebSocket connection error. Max reconnection attempts reached.' });
                            this.__socket = null;
                        }
                    }
                };

                socket.onerror = () => {
                    cb({ type: 'error', message: 'Skapi: WebSocket connection error.' });
                    throw new SkapiError(`Skapi: WebSocket connection error.`, { code: 'ERROR' });
                };
            }, delay);
        });
    }

    return this.__socket;
}

export async function closeRealtime(): Promise<void> {
    let socket: WebSocket = this.__socket ? await this.__socket : this.__socket;

    if (socket) {
        socket.close();
    }

    this.__socket = null;
    this.__socket_room = null;

    return null;
}

export async function postRealtime(message: any, recipient: string): Promise<{ type: 'success', message: 'Message sent.' }> {
    let socket: WebSocket = this.__socket ? await this.__socket : this.__socket;

    if (!socket) {
        throw new SkapiError(`No realtime connection. Execute connectRealtime() before this method.`, { code: 'INVALID_REQUEST' });
    }


    if (!recipient) {
        throw new SkapiError(`No recipient.`, { code: 'INVALID_REQUEST' });
    }

    message = extractFormData(message).data;

    if (socket.readyState === 1) {
        try {
            validator.UserId(recipient);
            socket.send(JSON.stringify({
                action: 'sendMessage',
                uid: recipient,
                content: message,
                token: this.session.accessToken.jwtToken
            }));

        } catch (err) {
            if (this.__socket_room !== recipient) {
                throw new SkapiError(`User has not joined to the recipient group. Run joinRealtime({ group: "${recipient}" })`, { code: 'INVALID_REQUEST' });
            }

            socket.send(JSON.stringify({
                action: 'broadcast',
                rid: recipient,
                content: message,
                token: this.session.accessToken.jwtToken
            }));
        }

        return { type: 'success', message: 'Message sent.' };
    }

    throw new SkapiError('Realtime connection is not open. Try reconnecting with connectRealtime().', { code: 'INVALID_REQUEST' });
}

export async function joinRealtime(params: { group?: string | null }): Promise<{ type: 'success', message: string }> {
    let socket: WebSocket = this.__socket ? await this.__socket : this.__socket;

    if (!socket) {
        throw new SkapiError(`No realtime connection. Execute connectRealtime() before this method.`, { code: 'INVALID_REQUEST' });
    }

    params = extractFormData(params).data;

    let { group = null } = params;

    if (!group && !this.__socket_room) {
        return { type: 'success', message: 'Left realtime message group.' }
    }

    if (group !== null && typeof group !== 'string') {
        throw new SkapiError(`"group" must be a string | null.`, { code: 'INVALID_PARAMETER' });
    }

    socket.send(JSON.stringify({
        action: 'joinRoom',
        rid: group,
        token: this.session.accessToken.jwtToken
    }));

    this.__socket_room = group;

    return { type: 'success', message: group ? `Joined realtime message group: "${group}".` : 'Left realtime message group.' }
}

export async function getRealtimeUsers(params: { group: string, user_id?: string }, fetchOptions?: FetchOptions): Promise<DatabaseResponse<{user_id: string;connection_id:string}[]>> {
    await this.__connection;

    params = validator.Params(
        params,
        {
            user_id: (v: string) => validator.UserId(v, 'User ID in "user_id"'),
            group: 'string'
        },
        ['group']
    );

    if (!params.group) {
        throw new SkapiError(`"group" is required.`, { code: 'INVALID_PARAMETER' });
    }

    if(!params.user_id) {
        if(__roomPending[params.group]) {
            return __roomPending[params.group];
        }
    }

    let req = request.bind(this)(
        'get-ws-group',
        params,
        {
            fetchOptions,
            auth: true,
            method: 'post'
        }
    ).then(res=>{
        res.list = res.list.map((v: any) => {
            let user_id = v.uid.split('#')[1];

            if(!params.user_id) {
                if(!__roomList[params.group]) {
                    __roomList[params.group] = {};
                }
                if(!__roomList[params.group][user_id]) {
                    __roomList[params.group][user_id] = [v.cid];
                }
                else if(!__roomList[params.group][user_id].includes(v.cid)) {
                    __roomList[params.group][user_id].push(v.cid);
                }
            }

            return {
                user_id,
                connection_id: v.cid
            }
        });

        return res;
    }).finally(()=>{
        delete __roomPending[params.group];
    });

    if(!params.user_id) {
        if(!__roomPending[params.group]) {
            __roomPending[params.group] = req;
        }
    }

    return req;
}

export async function getRealtimeGroups(
    params?: {
        /** Index name to search. */
        searchFor: 'group' | 'number_of_users';
        /** Index value to search. */
        value?: string | number;
        /** Search condition. */
        condition?: '>' | '>=' | '=' | '<' | '<=' | '!=' | 'gt' | 'gte' | 'eq' | 'lt' | 'lte' | 'ne';
        /** Range of search. */
        range?: string | number;
    } | null,
    fetchOptions?: FetchOptions
): Promise<DatabaseResponse<{ group: string; number_of_users: number; }>> {
    await this.__connection;

    if (!params) {
        params = { searchFor: 'group' };
    }

    params = validator.Params(
        params,
        {
            searchFor: ['group', 'number_of_users', () => 'group'],
            value: ['string', 'number', () => {
                if (params?.searchFor && params?.searchFor === 'number_of_users') {
                    return 0;
                }

                return ' ';
            }],
            condition: ['>', '>=', '=', '<', '<=', '!=', 'gt', 'gte', 'eq', 'lt', 'lte', 'ne'],
            range: ['string', 'number']
        }
    );

    if (!params.condition) {
        if (params.value === ' ' || !params.value) {
            params.condition = '>';
        }
        else {
            params.condition = '=';
        }
    }

    if (params.range && params.condition) {
        delete params.condition;
    }

    if (params.searchFor === 'number_of_users' && typeof params.value !== 'number') {
        throw new SkapiError(`"value" must be a number.`, { code: 'INVALID_PARAMETER' });
    }
    if (params.searchFor === 'group' && typeof params.value !== 'string') {
        throw new SkapiError(`"value" must be a string.`, { code: 'INVALID_PARAMETER' });
    }
    if (params.hasOwnProperty('range') && typeof params.range !== typeof params.value) {
        throw new SkapiError(`"range" must be a ${typeof params.value}.`, { code: 'INVALID_PARAMETER' });
    }

    let res = await request.bind(this)(
        'get-ws-group',
        params,
        {
            fetchOptions,
            auth: true,
            method: 'post'
        }
    )

    res.list = res.list.map((v: any) => {
        return {
            group: v.rid.split('#')[1],
            number_of_users: v.cnt
        }
    });

    return res;
}

